function [fitresult, gof] = createFit3(fullx, guess1)
%CREATEFIT3(FULLX,GUESS1)
%  Create a fit.
%
%  Data for 'exponentialFit' fit:
%      X Input : fullx
%      Y Output: guess1
%  Output:
%      fitresult : a fit object representing the fit.
%      gof : structure with goodness-of fit info.
%
%  See also FIT, CFIT, SFIT.

%  Auto-generated by MATLAB on 20-Aug-2015 12:18:19


%% Fit: 'exponentialFit'.
[xData, yData] = prepareCurveData( fullx, guess1 );

% Set up fittype and options.
ft = fittype( 'exp2' );
opts = fitoptions( 'Method', 'NonlinearLeastSquares' );
opts.Display = 'Off';
opts.Normalize = 'on';

% Fit model to data.
[fitresult, gof] = fit( xData, yData, ft, opts );

% Plot fit with data.
figure( 'Name', 'exponentialFit' );
h = plot( fitresult, xData, yData );
legend( h, 'guess1 vs. fullx', 'exponentialFit', 'Location', 'NorthEast' );
% Label axes
xlabel fullx
ylabel guess1
grid on


